mapper.update:
UPDATE reserve SET state=2
WHERE id in
#{id}

mapper.select:
SELECT * FROM reserve
WHERE patientId in
#{id}

SELECT * FROM reserve
WHERE doctorId in
#{id}

SELECT reserve.id id, patient.name patientName,doctor.name doctorName,reserve.NumberReminders,reserve.state,DATE_FORMAT(reserveTime,'%Y-%m-%d %H:%i') reserveTime,department.name departName,regis.value FROM reserve INNER JOIN patientInformation patient ON reserve.patientId=patient.id INNER JOIN doctorInformation doctor ON reserve.doctorId=doctor.id INNER JOIN department ON did=department.id INNER JOIN (SELECT r.id,VALUE FROM general,registrationLevel r WHERE NAME=coding) regis ON regis.id=registereId WHERE 1=1
AND patient.name LIKE CONCAT('%',#{patientName},'%')
AND doctor.name LIKE CONCAT('%',#{doctorName},'%')
AND doctor.did = #{did}
AND doctor.registereId = #{registereId}
AND reserve.id NOT IN (SELECT OrderForm.id FROM OrderForm) AND state=0

SELECT reserve.id id, patient.name patientName,doctor.name doctorName,reserve.NumberReminders,reserve.state,DATE_FORMAT(reserveTime,'%Y-%m-%d %H:%i') reserveTime,department.name departName,regis.value FROM reserve INNER JOIN patientInformation patient ON reserve.patientId=patient.id INNER JOIN doctorInformation doctor ON reserve.doctorId=doctor.id INNER JOIN department ON did=department.id INNER JOIN (SELECT r.id,VALUE FROM general,registrationLevel r WHERE NAME=coding) regis ON regis.id=registereId WHERE 1=1
AND patient.name LIKE CONCAT('%',#{patientName},'%')
AND doctor.name LIKE CONCAT('%',#{doctorName},'%')
AND doctor.did = #{did}
AND doctor.registereId = #{registereId}
AND reserve.id NOT IN (SELECT OrderForm.id FROM OrderForm) AND state=2

mapper.update:
UPDATE reserve SET state=0
WHERE id in
#{id}

mapper.select:
SELECT DATE_FORMAT(reserveTime,'%H:%i') reserveTime
FROM reserve
WHERE DATEDIFF(#{date},reserveTime)=0 AND doctorId=#{doctorId}

SELECT id
FROM reserve
WHERE DATE_FORMAT(#{reservetime},'%Y年%m月%d日 %H:%i')=DATE_FORMAT(reserveTime,'%Y年%m月%d日 %H:%i') AND doctorId=#{doctorid}
