mapper.update:
UPDATE reserve SET state=2
WHERE id in
#{id}

mapper.select:
SELECT * FROM reserve
WHERE patientId in
#{id}

SELECT * FROM reserve
WHERE doctorId in
#{id}

SELECT reserve.id id, patient.name patientName,doctor.name doctorName,reserve.NumberReminders,reserve.state,DATE_FORMAT(reserveTime,'%Y-%m-%d %H:%i') reserveTime,department.name departName,regis.value FROM reserve INNER JOIN patientInformation patient ON reserve.patientId=patient.id INNER JOIN doctorInformation doctor ON reserve.doctorId=doctor.id INNER JOIN department ON did=department.id INNER JOIN (SELECT r.id,VALUE FROM general,registrationLevel r WHERE NAME=coding) regis ON regis.id=registereId WHERE 1=1
AND patient.name LIKE CONCAT('%',#{patientName},'%')
AND doctor.name LIKE CONCAT('%',#{doctorName},'%')
AND doctor.did = #{did}
AND doctor.registereId = #{registereId}
AND reserve.id NOT IN (SELECT OrderForm.id FROM OrderForm) AND state=0

SELECT reserve.id id, patient.name patientName,doctor.name doctorName,reserve.NumberReminders,reserve.state,DATE_FORMAT(reserveTime,'%Y-%m-%d %H:%i') reserveTime,department.name departName,regis.value FROM reserve INNER JOIN patientInformation patient ON reserve.patientId=patient.id INNER JOIN doctorInformation doctor ON reserve.doctorId=doctor.id INNER JOIN department ON did=department.id INNER JOIN (SELECT r.id,VALUE FROM general,registrationLevel r WHERE NAME=coding) regis ON regis.id=registereId WHERE 1=1
AND patient.name LIKE CONCAT('%',#{patientName},'%')
AND doctor.name LIKE CONCAT('%',#{doctorName},'%')
AND doctor.did = #{did}
AND doctor.registereId = #{registereId}
AND reserve.id NOT IN (SELECT OrderForm.id FROM OrderForm) AND state=2

mapper.update:
UPDATE reserve SET state=0
WHERE id in
#{id}

mapper.select:
SELECT reserve.id,patient.name patientName,doctor.name doctorName,orderGeneral.value orderState, OrderForm.orderPrice totalPrice,DATE_FORMAT(OrderForm.operatingTime,'%Y年%m月%d日 %H时%i分%s秒') operatingTime, DATE_FORMAT(reserve.reserveTime,'%Y年%m月%d日 %H时%i分') reserveTime FROM reserve INNER JOIN OrderForm ON OrderForm.id=reserve.id AND (OrderForm.state='GST03' OR OrderForm.state='GST04') INNER JOIN general orderGeneral ON OrderForm.state=orderGeneral.coding INNER JOIN doctorInformation doctor ON reserve.doctorId=doctor.id INNER JOIN department ON doctor.did=department.id INNER JOIN patientInformation patient ON patient.id=reserve.patientId WHERE 1=1
AND doctor.name LIKE CONCAT('%',#{doctorName},'%')
AND reserve.patientId=#{patientId}

SELECT SUM(NumberReminders)
FROM reserve
WHERE state=0 AND patientId=#{patientId}
